        //0 = lose, 3 = draw, 6 = win
        // 1 = rock, 2 = paper, 3 = scissors
        private int Pt1CheckWinner(char op, char my)
        {
            //op plays rock
            if (op == 'A')
            {
                switch (my) 
                {
                    // rock draw
                    case 'X':
                        return 4;

                    // paper win
                    case 'Y':
                        return 8;

                    // scissors loss
                    case 'Z':
                        return 3;

                    default: 
                        return 0;
                }
            }
            //op plays paper
            else
            {
                if (op == 'B')
                {
                    switch (my)
                    {
                        //rock loss
                        case 'X':
                            return 1;

                        // paper draw
                        case 'Y':
                            return 5;

                        // scissors win
                        case 'Z':
                            return 9;

                        default: 
                            return 0;
                    }
                }
                // op plays scissors
                else
                {
                    switch (my)
                    {
                        //rock win
                        case 'X':
                            return 7;

                        //paper loss
                        case 'Y':
                            return 2;

                        // scissors draw
                        case 'Z':
                            return 6;

                        default: 
                            return 0;
                    }
                }
            }
        }


        //X = I lose, Y = draw, Z = I win
        private int Part2CheckWinner ( char op, char outcome)
        {
            //op plays rock
            if (op == 'A')
            {
                switch (outcome)
                {
                    // scissors loss
                    case 'X': return 3;

                    // rock draw
                    case 'Y': return 4;

                    // paper win
                    case 'Z': return 8;

                    default: return 0;
                }
            }
            //op plays paper
            else
            {
                if (op == 'B')
                {
                    switch (outcome)
                    {
                        // rock loss
                        case 'X':
                            return 1;

                        // paper draw
                        case 'Y':
                            return 5;

                        // scissors win
                        case 'Z':
                            return 9;

                        default:
                            return 0;
                    }
                }
                // op plays scissors
                else
                {
                    switch (outcome)
                    {
                        // paper loss
                        case 'X':
                            return 2;

                        // scissors draw
                        case 'Y':
                            return 6;

                        // rock win
                        case 'Z':
                            return 7;

                        default:
                            return 0;
                    }
                }
            }
        }
